module.exports = async function getContests() {
  const axios = require("axios");
  var result;

  await axios
    .get(
      "https://clist.by/api/v1/json/contest/?username=thewhitewolf17&api_key=e1bc7711e0e53b96b461603fccae5d8e67f887c4&limit=20&end__gt=2020-09-19T00%3A00%3A00"
    )
    .then((response) => {
      return response;
    })
    .then((contests) => {
      result = contests.data.objects;
    })
    .catch((err) => {
      console.log(err);
    });
  return result;
};
